var tipuesearch = {"pages":[{"title":"Writing mathematical expressions in Matplotlib","text":"It is possible to render LaTeX in matplotlib titles, legends and labels. You just need to surround the math text with dollar signs ($) and use raw strings (precede the quotes with an 'r'). Example: # plain text plt . title ( 'alpha > pi/2' ) # math text plt . title ( r '$\\alpha > \\pi/2$' ) More details can be found here .","tags":"python","url":"/writing-mathematical-expressions-in-matplotlib.html","loc":"/writing-mathematical-expressions-in-matplotlib.html"}]};